sR = 2 * Math.PI * r * encR / ticks_per_rev;
s = (sL + sR) / 2;
phi = (sR - sL) / d;
// speed
vL = sL / dt;
vR = sR / dt;
v = s / dt;
// speed in world
phi += tOld;
dx = Math.cos(phi) * v;
dy = Math.sin(phi) * v;
// position in world
xNew = xOld + dx * dt;
yNew = yOld + dy * dt;
tNew = phi;
// save old pose
context.set('encOldL',encNewL);
context.set('encOldR',encNewR);
context.set('xOld',xNew);
context.set('yOld',yNew);
context.set('tOld',tNew);
global.set('phi', phi);
// answer
